pragma solidity 0.6.11;
pragma experimental ABIEncoderV2;

import "./ILiquityBase.sol";
import "./IStabilityPool.sol";
import "./IZKUSDToken.sol";
import "./IZKToken.sol";
import "./IZKTStaking.sol";

// Common interface for the Trove Manager.
interface ITroveManager is ILiquityBase {
    // --- Events ---

    event BorrowerOperationsAddressChanged(
        address _newBorrowerOperationsAddress
    );
    event PriceFeedAddressChanged(address _newPriceFeedAddress);
    event ZKUSDTokenAddressChanged(address _newZKUSDTokenAddress);
    event ActivePoolAddressChanged(address _activePoolAddress);
    event DefaultPoolAddressChanged(address _defaultPoolAddress);
    event StabilityPoolAddressChanged(address _stabilityPoolAddress);
    event GasPoolAddressChanged(address _gasPoolAddress);
    event CollSurplusPoolAddressChanged(address _collSurplusPoolAddress);
    event SortedTrovesAddressChanged(address _sortedTrovesAddress);
    event ZKTokenAddressChanged(address _zkTokenAddress);
    event ZKTStakingAddressChanged(address _zktStakingAddress);

    event Liquidation(
        uint _liquidatedDebt,
        uint _liquidatedColl,
        uint _collGasCompensation,
        uint _ZKUSDGasCompensation
    );
    event Redemption(
        uint _attemptedZKUSDAmount,
        uint _actualZKUSDAmount,
        uint _ETHSent,
        uint _ETHFee
    );
    event TroveUpdated(
        address indexed _borrower,
        uint _debt,
        uint _coll,
        uint stake,
        uint8 operation
    );
    event TroveLiquidated(
        address indexed _borrower,
        uint _debt,
        uint _coll,
        uint8 operation
    );
    event BaseRateUpdated(uint _baseRate);
    event LastFeeOpTimeUpdated(uint _lastFeeOpTime);
    event TotalStakesUpdated(uint _newTotalStakes);
    event SystemSnapshotsUpdated(
        uint _totalStakesSnapshot,
        uint _totalCollateralSnapshot
    );
    event LTermsUpdated(uint256 _L_Native, uint256 _L_Debt);
    event TroveSnapshotsUpdated(
        address _borrower,
        uint256 _L_Native,
        uint256 _L_Debt
    );
    event TroveIndexUpdated(address _borrower, uint256 _newIndex);

    // --- Functions ---

    function setAddresses(
        address _borrowerOperationsAddress,
        address _activePoolAddress,
        address _defaultPoolAddress,
        address _stabilityPoolAddress,
        address _gasPoolAddress,
        address _collSurplusPoolAddress,
        address _priceFeedAddress,
        address _zkusdTokenAddress,
        address _sortedTrovesAddress,
        address _zkTokenAddress,
        address _zktStakingAddress
    ) external;

    function stabilityPool() external view returns (IStabilityPool);

    function zkusdToken() external view returns (IZKUSDToken);

    function zkToken() external view returns (IZKToken);

    function zktStaking() external view returns (IZKTStaking);

    function getTroveOwnersCount() external view returns (uint);

    function getTroveFromTroveOwnersArray(
        uint _index
    ) external view returns (address);

    function getNominalICR(address _borrower) external view returns (uint);

    function getCurrentICR(
        address _borrower,
        uint _price
    ) external view returns (uint);

    function liquidate(
        address _borrower,
        bytes[] calldata _updateData
    ) external payable;

    function liquidateTroves(
        uint _n,
        bytes[] calldata _updateData
    ) external payable;

    function batchLiquidateTroves(
        address[] calldata _troveArray,
        bytes[] calldata _updateData
    ) external payable;

    function redeemCollateral(
        uint _ZKUSDAmount,
        address _firstRedemptionHint,
        address _upperPartialRedemptionHint,
        address _lowerPartialRedemptionHint,
        uint _partialRedemptionHintNICR,
        uint _maxIterations,
        uint _maxFee,
        bytes[] calldata _updateData
    ) external payable;

    function updateStakeAndTotalStakes(
        address _borrower
    ) external returns (uint);

    function updateTroveRewardSnapshots(address _borrower) external;

    function addTroveOwnerToArray(
        address _borrower
    ) external returns (uint index);

    function applyPendingRewards(address _borrower) external;

    function getPendingETHReward(
        address _borrower
    ) external view returns (uint);

    function getPendingZKUSDDebtReward(
        address _borrower
    ) external view returns (uint);

    function hasPendingRewards(address _borrower) external view returns (bool);

    function getEntireDebtAndColl(
        address _borrower
    )
        external
        view
        returns (
            uint debt,
            uint coll,
            uint pendingZKUSDDebtReward,
            uint pendingETHReward
        );

    function closeTrove(address _borrower) external;

    function removeStake(address _borrower) external;

    function getRedemptionRate() external view returns (uint);

    function getRedemptionRateWithDecay() external view returns (uint);

    function getRedemptionFeeWithDecay(
        uint _ETHDrawn
    ) external view returns (uint);

    function getBorrowingRate() external view returns (uint);

    function getBorrowingRateWithDecay() external view returns (uint);

    function getBorrowingFee(uint ZKUSDDebt) external view returns (uint);

    function getBorrowingFeeWithDecay(
        uint _ZKUSDDebt
    ) external view returns (uint);

    function decayBaseRateFromBorrowing() external;

    function getTroveStatus(address _borrower) external view returns (uint);

    function getTroveStake(address _borrower) external view returns (uint);

    function getTroveDebt(address _borrower) external view returns (uint);

    function getTroveColl(address _borrower) external view returns (uint);

    function setTroveStatus(address _borrower, uint num) external;

    function increaseTroveColl(
        address _borrower,
        uint _collIncrease
    ) external returns (uint);

    function decreaseTroveColl(
        address _borrower,
        uint _collDecrease
    ) external returns (uint);

    function increaseTroveDebt(
        address _borrower,
        uint _debtIncrease
    ) external returns (uint);

    function decreaseTroveDebt(
        address _borrower,
        uint _collDecrease
    ) external returns (uint);

    function getTCR(uint _price) external view returns (uint);

    function checkRecoveryMode(uint _price) external view returns (bool);
}
