/*
 * The PriceFeed uses chainlink as primary oracle, and pyth-network as secondary.
 */
contract PriceFeedV2 is IPriceFeed, CheckContract, Ownable {
    using SafeMath for uint256;

    string public constant NAME = "PriceFeedV2";
    // Use to convert a price answer to an 18-digit precision uint
    uint public constant TARGET_DIGITS = 18;

    // Maximum time period allowed since Chainlink's latest round data timestamp, beyond which Chainlink is considered frozen.
    uint public constant TIMEOUT = 14400; // 4 hours: 60 * 60 * 4

    // Oracle response
    struct ChainlinkResponse {
        uint80 roundId;
        int256 answer;
        uint256 timestamp;
        bool success;
        uint8 decimals;
    }

    enum OracleStatus {
        Chainlink,
        PythNetwork
    }

    event LastGoodPriceUpdated(uint _lastGoodPrice);
    event PriceFeedStatusChanged(OracleStatus newStatus);

    IPyth public pythRouter;
    bytes32 public priceFeedId;
    AggregatorV3Interface public chainlinkAggregator;

    OracleStatus public status;

    // The last good price seen from an oracle
    uint public lastGoodPrice;

    // --- Dependency setters ---
    function setAddresses(
        bytes32 _priceFeedId,
        address _pythRouterAddress,
        address _chainlinkAggregatorAddress
    ) external onlyOwner {
        checkContract(_pythRouterAddress);
        checkContract(_chainlinkAggregatorAddress);

        priceFeedId = _priceFeedId;
        pythRouter = IPyth(_pythRouterAddress);
        chainlinkAggregator = AggregatorV3Interface(
            _chainlinkAggregatorAddress
        );

//        // Get an initial price from Chainlink to serve as first reference for lastGoodPrice
//        ChainlinkResponse
//        memory chainlinkResponse = _getCurrentChainlinkResponse();
//        ChainlinkResponse
//        memory prevChainlinkResponse = _getPrevChainlinkResponse(
//            chainlinkResponse.roundId,
//            chainlinkResponse.decimals
//        );
//
//        require(
//            !_chainlinkIsBroken(chainlinkResponse, prevChainlinkResponse) &&
//                !_chainlinkIsFrozen(chainlinkResponse),
//            "PriceFeed: Chainlink must be working and current"
//        );
//
//        _storeChainlinkPrice(chainlinkResponse);
    }

    function setOracleStatus(OracleStatus _status) external onlyOwner {
        status = _status;
        emit PriceFeedStatusChanged(_status);
    }

    /*
     * fetchPrice():
     * Returns the latest price obtained from the Oracle. Called by Liquity functions that require a current price.
     *
     * Also callable by anyone externally.
     *
     * Non-view function - it stores the last good price seen by Liquity.
     *
     * Uses a main oracle (Chainlink) and a fallback oracle (Tellor) in case Chainlink fails. If both fail,
     * it uses the last good price seen by Liquity.
     *
     */
    function fetchPrice() external override returns (uint) {
        _fetchPrice();
        return lastGoodPrice;
    }

    function fetchUpdateFee(
        bytes[] calldata _updateData
    ) external view override returns (uint) {
        if (status == OracleStatus.Chainlink) return 0;
        uint fee = pythRouter.getUpdateFee(_updateData);
        return fee;
    }

    function fetchPullPrice(
        uint fee,
        bytes[] calldata priceUpdateData
    ) external payable override returns (uint) {
        if (status == OracleStatus.Chainlink) {
            _fetchPrice();
        } else {
            _fetchPythPrice(fee, priceUpdateData);
        }
        return lastGoodPrice;
    }

    function _fetchPrice() internal returns (bool) {
        ChainlinkResponse
            memory chainlinkResponse = _getCurrentChainlinkResponse();
//        ChainlinkResponse
//            memory prevChainlinkResponse = _getPrevChainlinkResponse(
//                chainlinkResponse.roundId,
//                chainlinkResponse.decimals
//            );
//        if (
//            _chainlinkIsBroken(chainlinkResponse, prevChainlinkResponse) ||
//            _chainlinkIsFrozen(chainlinkResponse)
//        ) return false;
        _storeChainlinkPrice(chainlinkResponse);
        return true;
    }

    function _fetchPythPrice(
        uint fee,
        bytes[] calldata priceUpdateData
    ) internal returns (bool) {
        require(msg.value == fee, "Insufficient fee");
        pythRouter.updatePriceFeeds{value: fee}(priceUpdateData);
        PythStructs.Price memory retrievedPrice;
        try pythRouter.getEmaPrice(priceFeedId) returns (
            PythStructs.Price memory price
        ) {
            retrievedPrice = pythRouter.getEmaPrice(priceFeedId);
        } catch {
            retrievedPrice = pythRouter.getEmaPriceUnsafe(priceFeedId);
        }
        if (block.timestamp.sub(retrievedPrice.publishTime) > TIMEOUT) {
            return false;
        }
        uint baseConvertion = 10 **
            uint(int(TARGET_DIGITS) + retrievedPrice.expo);

        uint price = uint(retrievedPrice.price * int(baseConvertion));
        _storePrice(price);
        return true;
    }

    function _storePrice(uint _currentPrice) internal {
        lastGoodPrice = _currentPrice;
        emit LastGoodPriceUpdated(_currentPrice);
    }

    function _getCurrentChainlinkResponse()
        internal
        view
        returns (ChainlinkResponse memory chainlinkResponse)
    {
        // First, try to get current decimal precision:
        try chainlinkAggregator.decimals() returns (uint8 decimals) {
            // If call to Chainlink succeeds, record the current decimal precision
            chainlinkResponse.decimals = decimals;
        } catch {
            // If call to Chainlink aggregator reverts, return a zero response with success = false
            return chainlinkResponse;
        }

        // Secondly, try to get latest price data:
        try chainlinkAggregator.latestRoundData() returns (
            uint80 roundId,
            int256 answer,
            uint256 /* startedAt */,
            uint256 timestamp,
            uint80 /* answeredInRound */
        ) {
            // If call to Chainlink succeeds, return the response and success = true
            chainlinkResponse.roundId = roundId;
            chainlinkResponse.answer = answer;
            chainlinkResponse.timestamp = timestamp;
            chainlinkResponse.success = true;
            return chainlinkResponse;
        } catch {
            // If call to Chainlink aggregator reverts, return a zero response with success = false
            return chainlinkResponse;
        }
    }

    function _getPrevChainlinkResponse(
        uint80 _currentRoundId,
        uint8 _currentDecimals
    ) internal view returns (ChainlinkResponse memory prevChainlinkResponse) {
        /*
         * NOTE: Chainlink only offers a current decimals() value - there is no way to obtain the decimal precision used in a
         * previous round.  We assume the decimals used in the previous round are the same as the current round.
         */

        // Try to get the price data from the previous round:
        try chainlinkAggregator.getRoundData(_currentRoundId - 1) returns (
            uint80 roundId,
            int256 answer,
            uint256 /* startedAt */,
            uint256 timestamp,
            uint80 /* answeredInRound */
        ) {
            // If call to Chainlink succeeds, return the response and success = true
            prevChainlinkResponse.roundId = roundId;
            prevChainlinkResponse.answer = answer;
            prevChainlinkResponse.timestamp = timestamp;
            prevChainlinkResponse.decimals = _currentDecimals;
            prevChainlinkResponse.success = true;
            return prevChainlinkResponse;
        } catch {
            // If call to Chainlink aggregator reverts, return a zero response with success = false
            return prevChainlinkResponse;
        }
    }

    function _badChainlinkResponse(
        ChainlinkResponse memory _response
    ) internal view returns (bool) {
        // Check for response call reverted
        if (!_response.success) {
            return true;
        }
        // Check for an invalid roundId that is 0
        if (_response.roundId == 0) {
            return true;
        }
        // Check for an invalid timeStamp that is 0, or in the future
        if (_response.timestamp == 0 || _response.timestamp > block.timestamp) {
            return true;
        }
        // Check for non-positive price
        if (_response.answer <= 0) {
            return true;
        }

        return false;
    }

    /* Chainlink is considered broken if its current or previous round data is in any way bad. We check the previous round
     * for two reasons:
     *
     * 1) It is necessary data for the price deviation check in case 1,
     * and
     * 2) Chainlink is the PriceFeed's preferred primary oracle - having two consecutive valid round responses adds
     * peace of mind when using or returning to Chainlink.
     */
    function _chainlinkIsBroken(
        ChainlinkResponse memory _currentResponse,
        ChainlinkResponse memory _prevResponse
    ) internal view returns (bool) {
        return
            _badChainlinkResponse(_currentResponse) ||
            _badChainlinkResponse(_prevResponse);
    }

    function _chainlinkIsFrozen(
        ChainlinkResponse memory _response
    ) internal view returns (bool) {
        return block.timestamp.sub(_response.timestamp) > TIMEOUT;
    }

    function _storeChainlinkPrice(
        ChainlinkResponse memory _chainlinkResponse
    ) internal returns (uint) {
        uint scaledChainlinkPrice = _scaleChainlinkPriceByDigits(
            uint256(_chainlinkResponse.answer),
            _chainlinkResponse.decimals
        );
        _storePrice(scaledChainlinkPrice);

        return scaledChainlinkPrice;
    }

    function _scaleChainlinkPriceByDigits(
        uint _price,
        uint _answerDigits
    ) internal pure returns (uint) {
        /*
         * Convert the price returned by the Chainlink oracle to an 18-digit decimal for use by Liquity.
         * At date of Liquity launch, Chainlink uses an 8-digit price, but we also handle the possibility of
         * future changes.
         *
         */
        uint price;
        if (_answerDigits >= TARGET_DIGITS) {
            // Scale the returned price value down to Liquity's target precision
            price = _price.div(10 ** (_answerDigits - TARGET_DIGITS));
        } else if (_answerDigits < TARGET_DIGITS) {
            // Scale the returned price value up to Liquity's target precision
            price = _price.mul(10 ** (TARGET_DIGITS - _answerDigits));
        }
        return price;
    }
